    { value: 'BD', label: 'People\'s Republic of Bangladesh' }
  ];

  const validateField = (field: string, value: string): boolean => {
    let isValid = true;
    let errorMessage = '';

    switch (field) {
      case 'name':
        if (!value.trim()) {
          errorMessage = 'Name is required';
          isValid = false;
        }
        break;
      case 'email':
        if (!value.trim()) {
          errorMessage = 'Email is required';
          isValid = false;
        } else if (!/\S+@\S+\.\S+/.test(value)) {
          errorMessage = 'Please enter a valid email';
          isValid = false;
        }
        break;
      case 'phoneNumber':
        if (!value.trim()) {
          errorMessage = 'Phone number is required';
          isValid = false;
        }
        break;
      case 'nationalityCode':
        if (!value) {
          errorMessage = 'Nationality is required';
          isValid = false;
        }
        break;
      case 'typeCode':
        if (!value) {
          errorMessage = 'Lost item type is required';
          isValid = false;
        }
        break;
      case 'location':
        if (!value.trim()) {
          errorMessage = 'Location is required';
          isValid = false;
        }
        break;
      case 'description':
        if (!value.trim()) {
          errorMessage = 'Description is required';
          isValid = false;
        }
        break;
      case 'lostDate':
        if (!value) {
          errorMessage = 'Lost date is required';
          isValid = false;
        }
        break;
      case 'lostTime':
        if (!value) {
          errorMessage = 'Lost time is required';
          isValid = false;
        }
        break;
    }

    setErrors(prev => ({ ...prev, [field]: errorMessage }));
    return isValid;
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
    
    if (errors[field]) {
      validateField(field, value);
    }
  };

  const handleItemChange = (field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      items: {
        ...prev.items,
        [field]: value
      }
    }));
    
    if (errors[field]) {
      validateField(field, value);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Validate all fields
