          phoneNumber: '',
          nationalityCode: '',
          items: {
            typeCode: '',
            location: '',
            description: '',
            lostDate: '',
            lostTime: ''
          }
        });
        setErrors({});
        setShowSuccess(false);
      }, 2000);
    }

    setIsSubmitting(false);
  };

