          transition={{ duration: 0.8 }}
        >
          <motion.h2 
            className="text-4xl font-bold text-slate-800 mb-4"
            style={{ fontFamily: 'var(--font-amiri)' }}
          >
            Lost and Found
            <motion.span
              className="inline-block ml-3 text-amber-500"
