            </motion.span>
          </motion.h2>
          
          <motion.div 
            className="w-32 h-1 bg-gradient-to-r from-amber-400 to-amber-600 mx-auto rounded-full"
            initial={{ width: 0 }}
            whileInView={{ width: 128 }}
            transition={{ duration: 1, delay: 0.3 }}
          />
        </motion.div>

        {/* Instructions */}
        <motion.div
          className="bg-amber-50 border border-amber-200 rounded-lg p-6 mb-8"
          initial={{ opacity: 0, x: -20 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <div className="flex items-start space-x-3">
            <FaInfoCircle className="text-amber-500 text-xl mt-1 flex-shrink-0" />
            <p className="text-slate-700">
              Please fill-up the form below. We will contact you within 3 working days
            </p>
          </div>
        </motion.div>

        {/* Success Message */}
        <AnimatePresence>
          {showSuccess && (
            <motion.div
              className="fixed top-4 right-4 z-50 bg-green-500 text-white px-6 py-4 rounded-lg shadow-lg flex items-center space-x-3"
              initial={{ opacity: 0, x: 100 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 100 }}
              transition={{ duration: 0.3 }}
            >
              <FaSearch className="text-xl" />
              <div>
                <p className="font-bold">Report Submitted Successfully!</p>
                <p className="text-sm">We will contact you within 3 working days.</p>
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Form */}
        <motion.form
          onSubmit={handleSubmit}
          className="bg-white rounded-2xl shadow-xl p-8 border border-amber-100"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.4 }}
        >
          {/* Personal Information */}
          <h3 className="text-2xl font-bold text-slate-800 mb-6 pb-3 border-b border-amber-200">
            Personal Information
          </h3>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
            {/* Access Pass Number */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-slate-700">
                Access Pass Number (If exists)
              </label>
              <input
                type="text"
                value={formData.ticketNumber}
                onChange={(e) => handleInputChange('ticketNumber', e.target.value)}
                className="w-full px-4 py-2 border border-slate-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent transition-all"
                placeholder="Enter access pass number"
              />
            </div>

            {/* Name */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-slate-700">
                Name <span className="text-red-500">*</span>
              </label>
              <div className="relative">
                <FaUser className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400" />
                <input
                  type="text"
                  value={formData.name}
                  onChange={(e) => handleInputChange('name', e.target.value)}
                  onBlur={() => validateField('name', formData.name)}
                  className={`w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent transition-all ${
                    errors.name ? 'border-red-500' : 'border-slate-300'
                  }`}
                  placeholder="Enter your full name"
                />
              </div>
              {errors.name && (
                <p className="text-red-500 text-sm">{errors.name}</p>
              )}
            </div>

            {/* Email */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-slate-700">
                <div className="flex items-center space-x-1">
                  <FaInfoCircle className="text-amber-500 text-xs" />
                  <span>Email <span className="text-red-500">*</span></span>
                </div>
              </label>
              <div className="relative">
                <FaEnvelope className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400" />
                <input
                  type="email"
                  value={formData.email}
                  onChange={(e) => handleInputChange('email', e.target.value)}
                  onBlur={() => validateField('email', formData.email)}
                  className={`w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent transition-all ${
                    errors.email ? 'border-red-500' : 'border-slate-300'
                  }`}
                  placeholder="your.email@example.com"
                />
              </div>
              {errors.email && (
                <p className="text-red-500 text-sm">{errors.email}</p>
              )}
            </div>

            {/* Phone Number */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-slate-700">
                Phone Number <span className="text-red-500">*</span>
              </label>
              <div className="relative">
                <FaPhone className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400" />
                <input
                  type="tel"
                  value={formData.phoneNumber}
                  onChange={(e) => handleInputChange('phoneNumber', e.target.value)}
                  onBlur={() => validateField('phoneNumber', formData.phoneNumber)}
                  className={`w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent transition-all ${
                    errors.phoneNumber ? 'border-red-500' : 'border-slate-300'
                  }`}
                  placeholder="+1 (555) 123-4567"
                />
              </div>
              {errors.phoneNumber && (
                <p className="text-red-500 text-sm">{errors.phoneNumber}</p>
              )}
            </div>

            {/* Nationality */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-slate-700">
                Nationality <span className="text-red-500">*</span>
              </label>
              <div className="relative">
                <FaFlag className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400" />
                <select
                  value={formData.nationalityCode}
                  onChange={(e) => handleInputChange('nationalityCode', e.target.value)}
                  onBlur={() => validateField('nationalityCode', formData.nationalityCode)}
                  className={`w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent transition-all appearance-none ${
                    errors.nationalityCode ? 'border-red-500' : 'border-slate-300'
                  }`}
                >
                  <option value="">Choose Nationality</option>
                  {nationalities.map(nat => (
                    <option key={nat.value} value={nat.value}>
                      {nat.label}
                    </option>
                  ))}
                </select>
              </div>
              {errors.nationalityCode && (
                <p className="text-red-500 text-sm">{errors.nationalityCode}</p>
              )}
            </div>
          </div>

          {/* Lost Items Details */}
          <h3 className="text-2xl font-bold text-slate-800 mb-6 pb-3 border-b border-amber-200">
            Lost Items Details
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
            {/* Lost Item Type */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-slate-700">
                Lost item type <span className="text-red-500">*</span>
              </label>
              <select
                value={formData.items.typeCode}
                onChange={(e) => handleItemChange('typeCode', e.target.value)}
                onBlur={() => validateField('typeCode', formData.items.typeCode)}
                className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent transition-all appearance-none ${
                  errors.typeCode ? 'border-red-500' : 'border-slate-300'
                }`}
              >
                <option value="">Choose Type</option>
                {itemTypes.map(type => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
              {errors.typeCode && (
                <p className="text-red-500 text-sm">{errors.typeCode}</p>
              )}
            </div>

            {/* Lost Item Location */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-slate-700">
                Lost item Location/Area <span className="text-red-500">*</span>
              </label>
              <div className="relative">
                <FaMapMarkerAlt className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400" />
                <input
                  type="text"
                  value={formData.items.location}
                  onChange={(e) => handleItemChange('location', e.target.value)}
                  onBlur={() => validateField('location', formData.items.location)}
                  className={`w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent transition-all ${
                    errors.location ? 'border-red-500' : 'border-slate-300'
                  }`}
                  placeholder="e.g., Prayer Hall, Parking Area"
                />
              </div>
              {errors.location && (
                <p className="text-red-500 text-sm">{errors.location}</p>
              )}
            </div>

            {/* Lost Item Description */}
            <div className="space-y-2 lg:col-span-3">
              <label className="block text-sm font-medium text-slate-700">
                Lost Item Description <span className="text-red-500">*</span>
              </label>
              <textarea
                value={formData.items.description}
                onChange={(e) => handleItemChange('description', e.target.value)}
                onBlur={() => validateField('description', formData.items.description)}
                className={`w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent transition-all h-32 resize-none ${
                  errors.description ? 'border-red-500' : 'border-slate-300'
                }`}
                placeholder="Please provide detailed description of the lost item (color, brand, unique features, etc.)"
              />
              {errors.description && (
                <p className="text-red-500 text-sm">{errors.description}</p>
              )}
            </div>

            {/* Lost Date */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-slate-700">
                Lost Date <span className="text-red-500">*</span>
              </label>
              <div className="relative">
                <FaCalendarAlt className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400" />
                <input
                  type="date"
                  value={formData.items.lostDate}
                  onChange={(e) => handleItemChange('lostDate', e.target.value)}
                  onBlur={() => validateField('lostDate', formData.items.lostDate)}
                  className={`w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent transition-all ${
                    errors.lostDate ? 'border-red-500' : 'border-slate-300'
                  }`}
                  max={new Date().toISOString().split('T')[0]}
                />
              </div>
              {errors.lostDate && (
                <p className="text-red-500 text-sm">{errors.lostDate}</p>
              )}
            </div>

            {/* Lost Time */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-slate-700">
                Lost Time <span className="text-red-500">*</span>
              </label>
              <div className="relative">
                <FaClock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400" />
                <input
                  type="time"
                  value={formData.items.lostTime}
                  onChange={(e) => handleItemChange('lostTime', e.target.value)}
                  onBlur={() => validateField('lostTime', formData.items.lostTime)}
                  className={`w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent transition-all ${
                    errors.lostTime ? 'border-red-500' : 'border-slate-300'
                  }`}
                />
              </div>
              {errors.lostTime && (
                <p className="text-red-500 text-sm">{errors.lostTime}</p>
              )}
            </div>

            {/* Picture Upload */}
            <div className="space-y-2">
              <label className="block text-sm font-medium text-slate-700">
                Picture of the lost item (if exists)
              </label>
              <div className="relative">
                <FaCamera className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400" />
                <input
                  type="file"
                  accept="image/*"
                  className="w-full pl-10 pr-4 py-2 border border-slate-300 rounded-lg focus:ring-2 focus:ring-amber-500 focus:border-transparent transition-all file:mr-4 file:py-1 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-amber-50 file:text-amber-700 hover:file:bg-amber-100"
                />
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex justify-end space-x-4">
            <motion.button
              type="button"
              onClick={() => {
                setFormData({
                  ticketNumber: '',
                  name: '',
                  email: '',
                  phoneNumber: '',
                  nationalityCode: '',
                  items: {
                    typeCode: '',
                    location: '',
                    description: '',
                    lostDate: '',
                    lostTime: ''
                  }
                });
                setErrors({});
              }}
              className="px-6 py-3 bg-slate-200 text-slate-700 rounded-lg font-medium hover:bg-slate-300 transition-colors flex items-center space-x-2"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FaTimes />
              <span>Cancel</span>
            </motion.button>

            <motion.button
              type="submit"
              disabled={isSubmitting}
              className="px-8 py-3 bg-gradient-to-r from-amber-500 to-amber-600 text-white rounded-lg font-medium hover:from-amber-600 hover:to-amber-700 transition-all shadow-lg hover:shadow-amber-500/50 disabled:opacity-50 disabled:cursor-not-allowed flex items-center space-x-2"
              whileHover={{ scale: isSubmitting ? 1 : 1.05 }}
              whileTap={{ scale: isSubmitting ? 1 : 0.95 }}
            >
              {isSubmitting ? (
                <>
                  <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                  <span>Submitting...</span>
                </>
              ) : (
                <>
                  <FaSearch />
                  <span>Submit Report</span>
                </>
              )}
            </motion.button>
          </div>
        </motion.form>
      </div>
    </section>
  );
};

export default LostAndFound;

'use client';

import { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useLocale, useTranslations } from 'next-intl';
import { Link, usePathname, useRouter } from '@/i18n/routing';
import { 
  FiMenu, 
  FiX, 
  FiChevronDown, 
  FiFacebook, 
  FiTwitter, 
  FiInstagram, 
  FiYoutube,
  FiGlobe
} from 'react-icons/fi';

export default function Navbar() {
  const t = useTranslations('Navbar');
  const locale = useLocale();
  const [isOpen, setIsOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState<string | null>(null);
  const [mobileDropdowns, setMobileDropdowns] = useState<{ [key: string]: boolean }>({});
  const pathname = usePathname();
  const router = useRouter();
  const dropdownTimeoutRef = useRef<NodeJS.Timeout | undefined>(undefined);

  // Navigation Links with Dropdown Structure
  const navLinks = [
    {
      key: 'home',
      href: '/',
      label: 'Home',
      dropdown: null
    },
    {
      key: 'worshippers',
      href: '#',
      label: 'Worshippers',
      dropdown: [
        { href: '/worshippers/prayer-timings', label: 'Prayer Timings' },
        { href: '/worshippers/friday-sermon', label: 'Friday Sermon & Minbar Al Jami' },
        { href: '/worshippers/mosque-manners', label: 'Mosque Manners' },
        { href: '/worshippers/religious-programs', label: 'Religious Programs' },
        { href: '/worshippers/services-facilities', label: 'Services and Facilities' },
        { href: '/worshippers/getting-to-mosque', label: 'Getting To The Mosque' },
        { href: '/worshippers/e-visitor-guide', label: 'E-Visitor Guide' },
        { href: '/worshippers/souq-al-jami', label: 'Souq Al Jami' },
        { href: '/worshippers/jogging-track', label: "The Mosque's Jogging Track" },
        { href: '/worshippers/ramadan', label: 'Ramadan 1446 / 2025' }
      ]
    },
    {
      key: 'visitors',
      href: '#',
      label: 'Visitors',
      dropdown: [
        { href: '/visitors/mosque-manners', label: 'Mosque Manners' },
        { href: '/visitors/visiting-hours', label: 'Visiting Hours' },
        { href: '/visitors/book-visit', label: 'Book Your Visit' },
        { href: '/visitors/public-cultural-tours', label: 'Public Cultural Tours' },
        { href: '/visitors/private-cultural-tours', label: 'Private Cultural Tours' },
        { href: '/visitors/multimedia-guide', label: 'Multimedia E-Guide' },
        { href: '/visitors/sura-tour', label: 'Sura Tour' },
        { href: '/visitors/light-peace-museum', label: 'Light & Peace Museum' },
        { href: '/visitors/diya-experience', label: 'The 360-degree "Diya" Experience' },
        { href: '/visitors/visitor-journey', label: "Visitor's Journey" },
        { href: '/visitors/services-facilities', label: 'Services & Facilities' },
        { href: '/visitors/transportation', label: 'Transportation' },
        { href: '/visitors/faqs', label: 'FAQs' }
      ]
    },
    {
      key: 'news',
      href: '#',
      label: 'News & Media',
      dropdown: [
        { href: '/news/multimedia-gallery', label: 'View Our Multimedia Gallery' },
        { href: '/news/press-kit', label: 'Press Kit' },
        { href: '/news/coverage', label: 'View Our News and Media Coverage' }
      ]
    },
    {
      key: 'architecture',
      href: '#',
      label: 'Islamic Architecture',
      dropdown: [
        { href: '/architecture/overview', label: 'Overview' },
        { href: '/architecture/domes', label: 'The Domes' },
        { href: '/architecture/minarets', label: 'The Minarets' },
        { href: '/architecture/columns', label: 'The Columns' },
        { href: '/architecture/interior-columns', label: 'The Interior Columns' },
        { href: '/architecture/main-prayer-hall', label: 'The Main Prayer Hall' },
        { href: '/architecture/courtyard', label: 'The Courtyard' },
        { href: '/architecture/chandeliers', label: 'The Chandeliers' },
        { href: '/architecture/carpet', label: 'The Carpet' },
        { href: '/architecture/mihrab', label: 'The Mihrab' },
        { href: '/architecture/reflective-pools', label: 'Reflective Pools' },
        { href: '/architecture/glass-doors', label: 'The Glass Doors' },
        { href: '/architecture/interior-walls', label: 'The Interior Walls' },
        { href: '/architecture/clocks', label: 'The Clocks' },
        { href: '/architecture/qibla-wall', label: 'The Qibla Wall' }
      ]
    },
    {
      key: 'mosques',
      href: '#',
      label: 'Our Grand Mosques',
      dropdown: [
        { href: '/mosques/sheikh-zayed', label: 'Masjid Salman al Farsi' },
        { href: '/mosques/social-wall', label: 'Our Social Wall' }
      ]
    },
    {
      key: 'donate',
      href: '/donate',
      label: 'Donate',
      dropdown: null
    }
  ];

  // Handle dropdown hover
  const handleDropdownHover = (key: string | null) => {
    if (dropdownTimeoutRef.current) {
      clearTimeout(dropdownTimeoutRef.current);
    }
    setActiveDropdown(key);
  };

  const handleDropdownLeave = () => {
    dropdownTimeoutRef.current = setTimeout(() => {
      setActiveDropdown(null);
    }, 200);
  };

  // Handle language switch
  const handleLanguageSwitch = () => {
    const newLocale = locale === 'en' ? 'ar' : 'en';
    router.push(pathname.replace(`/${locale}`, `/${newLocale}`));
  };

  const toggleMobileDropdown = (key: string) => {
    setMobileDropdowns(prev => ({
      ...prev,
      [key]: !prev[key]
    }));
  };

  return (
    <motion.nav
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      className="fixed top-0 left-0 right-0 z-50 bg-white/95 backdrop-blur-md border-b border-amber-100 shadow-lg"
    >
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-14 lg:h-16">
          {/* Logo */}
          <motion.div 
            className="flex items-center"
            whileHover={{ scale: 1.05 }}
            transition={{ type: "spring", stiffness: 400, damping: 10 }}
          >
            <Link href="/" className="flex items-center space-x-2">
              <div className="w-8 h-8 lg:w-10 lg:h-10 bg-gradient-to-br from-amber-400 to-amber-600 rounded-lg flex items-center justify-center shadow-md">
                <span className="text-white font-bold text-sm lg:text-lg">MSF</span>
              </div>
              <span className="font-bold text-slate-800 text-sm lg:text-lg hidden sm:inline">
                Masjid Salman al Farsi
              </span>
            </Link>
          </motion.div>

          {/* Desktop Navigation */}
          <div className="hidden lg:flex items-center space-x-1 xl:space-x-2">
            {navLinks.map((link) => (
              <div
                key={link.key}
                className="relative"
                onMouseEnter={() => handleDropdownHover(link.key)}
                onMouseLeave={handleDropdownLeave}
              >
                {link.dropdown ? (
                  <button className="flex items-center space-x-1 px-3 py-2 text-slate-700 hover:text-amber-600 font-medium transition-all duration-200 hover:bg-amber-50 rounded-lg text-sm xl:text-base">
                    <span>{link.label}</span>
                    <FiChevronDown className={`w-4 h-4 transition-transform duration-200 ${activeDropdown === link.key ? 'rotate-180' : ''}`} />
                  </button>
                ) : (
                  <Link 
                    href={link.href} 
                    className="px-3 py-2 text-slate-700 hover:text-amber-600 font-medium transition-all duration-200 hover:bg-amber-50 rounded-lg text-sm xl:text-base"
                  >
                    {link.label}
                  </Link>
                )}
                
                {/* Dropdown Menu */}
                <AnimatePresence>
                  {activeDropdown === link.key && link.dropdown && (
                    <motion.div
                      initial={{ opacity: 0, y: -10, scale: 0.95 }}
                      animate={{ opacity: 1, y: 0, scale: 1 }}
                      exit={{ opacity: 0, y: -10, scale: 0.95 }}
                      transition={{ duration: 0.2, ease: "easeOut" }}
                      className="absolute top-full left-0 mt-1 w-64 bg-white rounded-xl shadow-xl border border-amber-100 overflow-hidden"
                    >
                      <div className="py-2">
                        {link.dropdown.map((item, index) => (
                          <Link
                            key={index}
                            href={item.href}
                            className="block px-4 py-3 text-sm text-slate-700 hover:bg-amber-50 hover:text-amber-600 transition-colors duration-200"
                          >
                            {item.label}
                          </Link>
                        ))}
                      </div>
                    </motion.div>
                  )}
                </AnimatePresence>
              </div>
            ))}
          </div>

          {/* Right Section */}
          <div className="flex items-center space-x-2 lg:space-x-4">
            {/* Language Switcher */}
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={handleLanguageSwitch}
              className="flex items-center space-x-1 px-3 py-1.5 lg:px-4 lg:py-2 bg-amber-100 hover:bg-amber-200 text-amber-700 rounded-lg transition-colors duration-200 group"
            >
              <FiGlobe className="w-3 h-3 sm:w-4 sm:h-4 transition-transform duration-300 group-hover:rotate-180" />
              <span className="font-medium text-xs sm:text-sm hidden sm:inline">
                {locale === 'ar' ? 'EN' : 'AR'}
              </span>
            </motion.button>

            {/* Mobile Hamburger */}
            <button 
              className="lg:hidden p-2 text-slate-700 hover:text-amber-500 transition-colors duration-200"
              onClick={() => {
                setIsOpen(!isOpen);
              }}
              aria-label="Menu"
            >
              {isOpen ? <FiX className="w-5 h-5 sm:w-6 sm:h-6" /> : <FiMenu className="w-5 h-5 sm:w-6 sm:h-6" />}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Sidebar Overlay - NEW IMPLEMENTATION */}
      {isOpen && (
        <div className="fixed inset-0 z-[9999] lg:hidden">
          {/* Background Overlay */}
          <div 
